const API = 'https://api.nhomnhom.com';
const api = async (path, opts = {}) => {
  const token = localStorage.getItem('linguaToken');
  const headers = { 'Content-Type': 'application/json', ...(token ? { Authorization: `Bearer ${token}` } : {}) };
  const res = await fetch(`${API}${path}`, { ...opts, headers: { ...headers, ...opts.headers } });
  const data = await res.json();
  if (!res.ok) throw new Error(data.error || 'API error');
  return data;
};

export default function TranslatorPlatform() {
  const [currentPage, setCurrentPage] = useState(localStorage.getItem('linguaToken') ? 'dashboard' : 'home');
  const [user, setUser] = useState(null);
  const [groups, setGroups] = useState([]);
  const [billing, setBilling] = useState(null);
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);
  const navigate = (page) => { setCurrentPage(page); setMobileMenuOpen(false); window.scrollTo(0, 0); };

  const handleLogout = () => { localStorage.removeItem('linguaToken'); setUser(null); setGroups([]); setBilling(null); navigate('home'); };

  // Fetch dashboard data from live API
  const refreshData = async () => {
    if (!localStorage.getItem('linguaToken')) return;
    try {
      const data = await api('/api/me');
      setUser(prev => ({ ...prev, ...data.user, botActive: data.billing.activeGroups > 0 }));
      setGroups(data.groups);
      setBilling(data.billing);
    } catch (e) { console.error('Refresh failed:', e); }
  };

  // Poll every 15s when on dashboard
  useEffect(() => {
    if (currentPage === 'dashboard' && localStorage.getItem('linguaToken')) {
      refreshData();
      const iv = setInterval(refreshData, 15000);
      return () => clearInterval(iv);
    }
  }, [currentPage]);

  // Auto-load user on mount if token exists
  useEffect(() => { if (localStorage.getItem('linguaToken')) refreshData(); }, []);

  const handleSignup = async (userData) => {
    try {
      const data = await api('/api/signup', { method: 'POST', body: JSON.stringify({ email: userData.email, password: userData.password, name: userData.name }) });
      localStorage.setItem('linguaToken', data.token);
      setUser({ ...data.user, botActive: false });
      setCurrentPage('dashboard');
      return null;
    } catch (e) { return e.message; }
  };

  const handleLogin = async (email, password) => {
    try {
      const data = await api('/api/login', { method: 'POST', body: JSON.stringify({ email, password }) });
      localStorage.setItem('linguaToken', data.token);
      setUser({ ...data.user, botActive: false });
      setCurrentPage('dashboard');
      await refreshData();
      return null;
    } catch (e) { return e.message; }
  };

  const navItems = [{ label: 'Features', page: 'home', section: 'features' },{ label: 'Pricing', page: 'home', section: 'pricing' },{ label: 'Case Studies', page: 'home', section: 'casestudies' }];
  const handleNavClick = (item) => { setMobileMenuOpen(false); if (item.section && currentPage === 'home') { document.getElementById(item.section)?.scrollIntoView({ behavior: 'smooth' }); } else if (item.section) { setCurrentPage('home'); setTimeout(() => document.getElementById(item.section)?.scrollIntoView({ behavior: 'smooth' }), 100); } else { navigate(item.page); } };

  return (
    <div className="min-h-screen bg-[#0a0a0b] text-white overflow-x-hidden">
      <style>{`
        @import url('https://fonts.googleapis.com/css2?family=Inter:wght@300;400;500;600;700;800;900&display=swap');
        *{box-sizing:border-box;margin:0;padding:0}body{font-family:'Inter',-apple-system,sans-serif;background:#0a0a0b}
        .gradient-text{background:linear-gradient(135deg,#a855f7 0%,#6366f1 50%,#06b6d4 100%);-webkit-background-clip:text;-webkit-text-fill-color:transparent}
        .gradient-text-warm{background:linear-gradient(135deg,#f97316 0%,#ec4899 50%,#a855f7 100%);-webkit-background-clip:text;-webkit-text-fill-color:transparent}
        .mesh-gradient{position:absolute;width:100%;height:100%;background:radial-gradient(at 40% 20%,rgba(168,85,247,.3) 0,transparent 50%),radial-gradient(at 80% 0%,rgba(6,182,212,.2) 0,transparent 50%),radial-gradient(at 0% 50%,rgba(249,115,22,.15) 0,transparent 50%),radial-gradient(at 80% 50%,rgba(236,72,153,.15) 0,transparent 50%),radial-gradient(at 0% 100%,rgba(99,102,241,.2) 0,transparent 50%);filter:blur(100px)}
        .grid-pattern{background-image:linear-gradient(rgba(255,255,255,.03) 1px,transparent 1px),linear-gradient(90deg,rgba(255,255,255,.03) 1px,transparent 1px);background-size:60px 60px}
        .animate-pulse-slow{animation:ps 4s ease-in-out infinite}@keyframes ps{0%,100%{opacity:.4}50%{opacity:.8}}
        .animate-slide-up{animation:su .8s ease-out forwards;opacity:0}@keyframes su{from{opacity:0;transform:translateY(40px)}to{opacity:1;transform:translateY(0)}}
        .stagger-1{animation-delay:.1s}.stagger-2{animation-delay:.2s}.stagger-3{animation-delay:.3s}.stagger-4{animation-delay:.4s}.stagger-5{animation-delay:.5s}
        @keyframes mq{0%{transform:translateX(0)}100%{transform:translateX(-50%)}}.marquee{animation:mq 30s linear infinite}
        .glow-line{height:1px;background:linear-gradient(90deg,transparent,rgba(168,85,247,.5),rgba(6,182,212,.5),transparent)}
        .pricing-card{background:linear-gradient(135deg,rgba(255,255,255,.04) 0%,rgba(255,255,255,.01) 100%);border:1px solid rgba(255,255,255,.08)}
        .telegram-bg{background-color:#0b141a;background-image:url("data:image/svg+xml,%3Csvg width='60' height='60' viewBox='0 0 60 60' xmlns='http://www.w3.org/2000/svg'%3E%3Cg fill='none' fill-rule='evenodd'%3E%3Cg fill='%23ffffff' fill-opacity='0.02'%3E%3Cpath d='M36 34v-4h-2v4h-4v2h4v4h2v-4h4v-2h-4zm0-30V0h-2v4h-4v2h4v4h2V6h4V4h-4zM6 34v-4H4v4H0v2h4v4h2v-4h4v-2H6zM6 4V0H4v4H0v2h4v4h2V6h4V4H6z'/%3E%3C/g%3E%3C/g%3E%3C/svg%3E")}
        .chat-scroll{overflow-y:auto;scrollbar-width:none;-ms-overflow-style:none}.chat-scroll::-webkit-scrollbar{display:none}
        @keyframes tb{0%,60%,100%{transform:translateY(0)}30%{transform:translateY(-4px)}}.typing-indicator span:nth-child(1){animation:tb 1.4s infinite}.typing-indicator span:nth-child(2){animation:tb 1.4s infinite .2s}.typing-indicator span:nth-child(3){animation:tb 1.4s infinite .4s}
        @keyframes ma{from{opacity:0;transform:translateY(10px)}to{opacity:1;transform:translateY(0)}}.message-appear{animation:ma .3s ease-out}
        .phone-frame{background:linear-gradient(145deg,rgba(40,40,45,1) 0%,rgba(25,25,30,1) 50%,rgba(40,40,45,1) 100%);box-shadow:0 25px 60px rgba(0,0,0,.5),inset 0 1px 0 rgba(255,255,255,.1),0 0 80px rgba(168,85,247,.2)}
        .bot-badge{background:linear-gradient(135deg,#8b5cf6 0%,#06b6d4 100%)}.translation-bubble{background:linear-gradient(135deg,rgba(139,92,246,.15) 0%,rgba(6,182,212,.1) 100%);border:1px solid rgba(139,92,246,.3)}
        @keyframes spin{to{transform:rotate(360deg)}}.animate-spin{animation:spin 1s linear infinite}
      `}</style>
      <nav className="fixed top-0 left-0 right-0 z-50 backdrop-blur-xl bg-[#0a0a0b]/80 border-b border-white/5">
        <div className="max-w-7xl mx-auto px-6" style={{height:'60px',display:'flex',alignItems:'center',justifyContent:'space-between'}}>
          <div style={{display:'flex',alignItems:'center',gap:'36px'}}>
            <div className="cursor-pointer flex items-center gap-2.5" onClick={() => navigate('home')}><div className="w-8 h-8 rounded-lg bg-gradient-to-br from-purple-500 to-cyan-500 flex items-center justify-center"><Globe className="w-4 h-4 text-white" /></div><span className="font-semibold text-lg tracking-tight">LinguaBot</span></div>
            {navItems.map(item => (<button key={item.label} onClick={() => handleNavClick(item)} className="text-gray-300 hover:text-white transition-colors" style={{cursor:'pointer',fontSize:'14px',fontWeight:500,background:'none',border:'none',padding:0}}>{item.label}</button>))}
          </div>
          {user || localStorage.getItem('linguaToken') ? (<div style={{display:'flex',gap:'8px'}}><button onClick={() => navigate('dashboard')} style={{background:'rgba(255,255,255,0.95)',border:'none',borderRadius:'8px',padding:'8px 22px',color:'#111',fontSize:'14px',fontWeight:500,cursor:'pointer'}}>Dashboard</button><button onClick={handleLogout} style={{background:'none',border:'1px solid rgba(255,255,255,0.15)',borderRadius:'8px',padding:'8px 16px',color:'#999',fontSize:'13px',fontWeight:500,cursor:'pointer'}}>Log out</button></div>) : (<button onClick={() => navigate('signup')} style={{background:'rgba(255,255,255,0.95)',border:'none',borderRadius:'8px',padding:'8px 22px',color:'#111',fontSize:'14px',fontWeight:500,cursor:'pointer'}}>Sign up</button>)}
        </div>
      </nav>
      {currentPage === 'home' && <HomePage onNavigate={navigate} />}
      {currentPage === 'signup' && <SignupPage onNavigate={navigate} onSignup={handleSignup} />}
      {currentPage === 'login' && <LoginPage onNavigate={navigate} onLogin={handleLogin} />}
      {currentPage === 'dashboard' && <Dashboard user={user} setUser={setUser} groups={groups} billing={billing} onNavigate={navigate} onRefresh={refreshData} onLogout={handleLogout} />}
      {currentPage === 'billing' && <BillingPage user={user} setUser={setUser} onNavigate={navigate} onRefresh={refreshData} />}
      {currentPage === 'about' && <AboutPage onNavigate={navigate} />}
      {currentPage === 'blog' && <BlogPage onNavigate={navigate} />}
      {currentPage === 'terms' && <TermsPage onNavigate={navigate} />}
      {currentPage === 'privacy' && <PrivacyPage onNavigate={navigate} />}
    </div>
  );
}

function TelegramDemo() {
  const [messages, setMessages] = useState([]); const [currentStep, setCurrentStep] = useState(0); const [isTyping, setIsTyping] = useState(null); const chatRef = useRef(null);
  const conv = [{id:1,type:'message',sender:'alex',name:'Alex',text:"Hey Minh! How's the project going?",time:'10:32 AM'},{id:2,type:'translation',sender:'bot',originalLang:'EN',targetLang:'VI',text:"ChÃ o Minh! Dá»± Ã¡n tháº¿ nÃ o rá»“i?",time:'10:32 AM'},{id:3,type:'message',sender:'minh',name:'Minh',text:"Äang tiáº¿n triá»ƒn tá»‘t. TÃ´i Ä‘Ã£ hoÃ n thÃ nh backend rá»“i.",time:'10:33 AM'},{id:4,type:'translation',sender:'bot',originalLang:'VI',targetLang:'EN',text:"Going well. I finished the backend already.",time:'10:33 AM'},{id:5,type:'message',sender:'alex',name:'Alex',text:"Great! Send me the docs this afternoon?",time:'10:34 AM'},{id:6,type:'translation',sender:'bot',originalLang:'EN',targetLang:'VI',text:"Tuyá»‡t! Gá»­i tÃ i liá»‡u chiá»u nay nhÃ©?",time:'10:34 AM'},{id:7,type:'message',sender:'minh',name:'Minh',text:"ÄÆ°á»£c! Gá»­i trong 2 tiáº¿ng ná»¯a ðŸ™",time:'10:35 AM'},{id:8,type:'translation',sender:'bot',originalLang:'VI',targetLang:'EN',text:"Sure! I'll send in 2 hours ðŸ™",time:'10:35 AM'}];
  useEffect(() => { if(chatRef.current) chatRef.current.scrollTop = chatRef.current.scrollHeight; }, [messages]);
  useEffect(() => { if(currentStep >= conv.length){ const t=setTimeout(()=>{setMessages([]);setCurrentStep(0)},3000); return ()=>clearTimeout(t); } const msg=conv[currentStep]; const td=msg.type==='translation'?600:1200; const ts=msg.type==='translation'?'bot':msg.sender; const t1=setTimeout(()=>setIsTyping(ts),currentStep===0?500:200); const md=msg.type==='translation'?1200:2000; const t2=setTimeout(()=>{setIsTyping(null);setMessages(p=>[...p,msg]);setCurrentStep(p=>p+1)},td+md); return ()=>{clearTimeout(t1);clearTimeout(t2)}; }, [currentStep]);
  return (<div className="phone-frame rounded-[2.5rem] p-2 mx-auto w-full max-w-[340px]"><div className="bg-[#0b141a] rounded-[2rem] overflow-hidden relative"><div className="bg-[#202c33] px-3 py-2 flex items-center gap-2"><ArrowLeft className="w-4 h-4 text-gray-400" /><div className="w-8 h-8 rounded-full bg-gradient-to-br from-purple-500 to-cyan-500 flex items-center justify-center"><span className="text-white font-semibold text-xs">P</span></div><div className="flex-1 min-w-0"><h2 className="text-white font-medium text-xs truncate">Project Alpha Team</h2><p className="text-gray-400 text-[10px] truncate">Alex, Minh, LinguaBot</p></div><Video className="w-4 h-4 text-gray-400" /><Phone className="w-4 h-4 text-gray-400" /></div><div ref={chatRef} className="telegram-bg chat-scroll h-[380px] px-2 py-3 flex flex-col"><div className="flex justify-center mb-3"><span className="px-2 py-0.5 bg-[#182229] rounded text-[10px] text-gray-400">Today</span></div><div className="flex-1 space-y-1.5">{messages.map(msg=>(<div key={msg.id} className="message-appear">{msg.type==='message'?<MsgB m={msg}/>:<TransB m={msg}/>}</div>))}{isTyping&&<TypD s={isTyping}/>}</div></div><div className="bg-[#202c33] px-2 py-1.5 flex items-center gap-1.5"><Smile className="w-5 h-5 text-gray-400" /><div className="flex-1 bg-[#2a3942] rounded-full px-3 py-1.5"><span className="text-gray-500 text-xs">Type a message</span></div><div className="w-8 h-8 rounded-full bg-[#00a884] flex items-center justify-center"><Mic className="w-4 h-4 text-white" /></div></div></div></div>);
}
function MsgB({m}){const a=m.sender==='alex';return(<div className={`flex ${a?'justify-end':'justify-start'}`}><div className={`max-w-[80%] rounded-lg px-2.5 py-1.5 ${a?'bg-[#005c4b] rounded-tr-none':'bg-[#202c33] rounded-tl-none'}`}><p className={`text-[10px] font-medium mb-0.5 ${a?'text-emerald-300':'text-purple-400'}`}>{m.name}</p><p className="text-white text-xs leading-relaxed">{m.text}</p><div className="flex items-center justify-end gap-1 mt-0.5"><span className="text-[8px] text-gray-400">{m.time}</span>{a&&<CheckCheck className="w-3 h-3 text-cyan-400"/>}</div></div></div>)}
function TransB({m}){return(<div className="flex justify-center my-1.5"><div className="translation-bubble rounded-lg px-2.5 py-1.5 max-w-[85%]"><div className="flex items-center gap-1.5 mb-1"><div className="bot-badge w-4 h-4 rounded-full flex items-center justify-center"><Globe className="w-2.5 h-2.5 text-white"/></div><span className="text-[9px] text-purple-300 font-medium">LinguaBot</span><span className="text-[9px] text-gray-500">{m.originalLang} â†’ {m.targetLang}</span></div><p className="text-white text-xs leading-relaxed">{m.text}</p><div className="flex items-center justify-end mt-0.5"><span className="text-[8px] text-gray-500">{m.time}</span></div></div></div>)}
function TypD({s}){const b=s==='bot',a=s==='alex';if(b)return(<div className="flex justify-center my-1.5"><div className="translation-bubble rounded-lg px-2.5 py-2"><div className="flex items-center gap-1.5"><div className="bot-badge w-4 h-4 rounded-full flex items-center justify-center"><Globe className="w-2.5 h-2.5 text-white"/></div><span className="text-[9px] text-purple-300 font-medium">LinguaBot</span><div className="typing-indicator flex gap-0.5 ml-1"><span className="w-1.5 h-1.5 bg-purple-400 rounded-full"/><span className="w-1.5 h-1.5 bg-purple-400 rounded-full"/><span className="w-1.5 h-1.5 bg-purple-400 rounded-full"/></div></div></div></div>);return(<div className={`flex ${a?'justify-end':'justify-start'}`}><div className={`rounded-lg px-3 py-2 ${a?'bg-[#005c4b]':'bg-[#202c33]'}`}><div className="typing-indicator flex gap-1"><span className={`w-1.5 h-1.5 rounded-full ${a?'bg-emerald-300':'bg-gray-400'}`}/><span className={`w-1.5 h-1.5 rounded-full ${a?'bg-emerald-300':'bg-gray-400'}`}/><span className={`w-1.5 h-1.5 rounded-full ${a?'bg-emerald-300':'bg-gray-400'}`}/></div></div></div>)}

