function Foot({onNavigate}){return(<footer className="border-t border-white/5 py-16"><div className="max-w-6xl mx-auto px-6"><div className="grid md:grid-cols-4 gap-12 mb-12"><div><div className="flex items-center gap-3 mb-4 cursor-pointer" onClick={()=>onNavigate('home')}><div className="w-10 h-10 rounded-xl bg-gradient-to-br from-purple-500 to-cyan-500 flex items-center justify-center"><Globe className="w-5 h-5"/></div><span className="font-bold text-xl">LinguaBot</span></div><p className="text-sm text-gray-500 leading-relaxed">AI-powered Telegram translation for teams and individuals worldwide.</p></div><div><h4 className="font-semibold text-sm mb-4 text-gray-300">Product</h4><div className="space-y-3 text-sm text-gray-500">{[['Features','home'],['Pricing','home'],['Blog','blog']].map(([l,p])=>(<button key={l} onClick={()=>onNavigate(p)} className="block hover:text-white transition-colors" style={{cursor:'pointer'}}>{l}</button>))}</div></div><div><h4 className="font-semibold text-sm mb-4 text-gray-300">Company</h4><div className="space-y-3 text-sm text-gray-500">{[['About','about'],['Careers','about'],['Contact','about']].map(([l,p])=>(<button key={l} onClick={()=>onNavigate(p)} className="block hover:text-white transition-colors" style={{cursor:'pointer'}}>{l}</button>))}</div></div><div><h4 className="font-semibold text-sm mb-4 text-gray-300">Legal</h4><div className="space-y-3 text-sm text-gray-500">{[['Privacy Policy','privacy'],['Terms of Service','terms']].map(([l,p])=>(<button key={l} onClick={()=>onNavigate(p)} className="block hover:text-white transition-colors" style={{cursor:'pointer'}}>{l}</button>))}</div></div></div><div className="pt-8 border-t border-white/5 flex flex-col md:flex-row items-center justify-between gap-4"><div className="text-sm text-gray-600">Â© 2026 LinguaBot. All rights reserved.</div><div className="flex items-center gap-6 text-sm text-gray-500"><button onClick={()=>onNavigate('privacy')} className="hover:text-white transition-colors" style={{cursor:'pointer'}}>Privacy</button><button onClick={()=>onNavigate('terms')} className="hover:text-white transition-colors" style={{cursor:'pointer'}}>Terms</button></div></div></div></footer>)}

function SignupPage({onNavigate,onSignup}){const[step,setStep]=useState(1);const[fd,setFd]=useState({email:'',password:'',name:''});const[error,setError]=useState('');const[loading,setLoading]=useState(false);const go=async()=>{if(step===1){if(!fd.email||!fd.password||fd.password.length<6)return;setStep(2)}else{setLoading(true);setError('');const err=await onSignup({name:fd.name||fd.email.split('@')[0],email:fd.email,password:fd.password});if(err)setError(err);setLoading(false)}};const kd=e=>{if(e.key==='Enter')go()};return(<div className="min-h-screen flex items-center justify-center px-6 py-32 relative"><div className="absolute inset-0 bg-gradient-to-b from-purple-500/5 via-transparent to-transparent"/><div className="w-full max-w-md relative z-10"><div className="text-center mb-8"><div className="w-16 h-16 rounded-2xl bg-gradient-to-br from-purple-500 to-cyan-500 flex items-center justify-center mx-auto mb-6"><Globe className="w-8 h-8"/></div><h1 className="text-3xl font-bold mb-2">Create your account</h1><p className="text-gray-400">Get started with your free hour</p></div><div className="rounded-2xl p-8" style={S.card}>{error&&<div className="mb-6 px-4 py-3 rounded-xl flex items-center gap-3" style={{background:'rgba(239,68,68,0.1)',border:'1px solid rgba(239,68,68,0.3)'}}><AlertCircle className="w-5 h-5 text-red-400 shrink-0"/><span className="text-sm text-red-300">{error}</span></div>}{step===1?(<><div className="mb-6"><label className="block text-sm font-medium text-gray-300 mb-2">Email</label><input type="email" value={fd.email} onChange={e=>setFd({...fd,email:e.target.value})} onKeyDown={kd} style={S.input} className="w-full px-4 py-3 rounded-xl" placeholder="you@example.com"/></div><div className="mb-6"><label className="block text-sm font-medium text-gray-300 mb-2">Password</label><input type="password" value={fd.password} onChange={e=>setFd({...fd,password:e.target.value})} onKeyDown={kd} style={S.input} className="w-full px-4 py-3 rounded-xl" placeholder="Min 6 characters"/>{fd.password&&fd.password.length<6&&<p className="text-xs text-orange-400 mt-1">Password must be at least 6 characters</p>}</div></>):(<div className="mb-6"><label className="block text-sm font-medium text-gray-300 mb-2">Your name</label><input type="text" value={fd.name} onChange={e=>setFd({...fd,name:e.target.value})} onKeyDown={kd} style={S.input} className="w-full px-4 py-3 rounded-xl" placeholder="What should we call you?"/></div>)}<button onClick={go} disabled={loading} style={S.btnP} className="w-full py-4 rounded-xl font-semibold flex items-center justify-center gap-2 text-white">{loading?<><RefreshCw className="w-5 h-5 animate-spin"/>Creating...</>:<>{step===1?'Continue':'Create Account'}<ArrowRight className="w-5 h-5"/></>}</button><div className="mt-6 pt-6 border-t border-white/10 text-center"><p className="text-gray-400 text-sm">Already have an account? <button onClick={()=>onNavigate('login')} className="text-purple-400 hover:text-purple-300" style={{cursor:'pointer'}}>Sign in</button></p></div></div><div className="mt-8 flex items-center justify-center gap-2 text-sm text-gray-500"><Gift className="w-4 h-4 text-green-400"/><span>First hour of translation is free</span></div></div></div>)}

function LoginPage({onNavigate,onLogin}){const[fd,setFd]=useState({email:'',password:''});const[error,setError]=useState('');const[loading,setLoading]=useState(false);const go=async()=>{if(!fd.email||!fd.password){setError('Please fill in all fields.');return}setLoading(true);setError('');const err=await onLogin(fd.email,fd.password);if(err)setError(err);setLoading(false)};const kd=e=>{if(e.key==='Enter')go()};return(<div className="min-h-screen flex items-center justify-center px-6 py-32 relative"><div className="absolute inset-0 bg-gradient-to-b from-cyan-500/5 via-transparent to-transparent"/><div className="w-full max-w-md relative z-10"><div className="text-center mb-8"><div className="w-16 h-16 rounded-2xl bg-gradient-to-br from-purple-500 to-cyan-500 flex items-center justify-center mx-auto mb-6"><Globe className="w-8 h-8"/></div><h1 className="text-3xl font-bold mb-2">Welcome back</h1><p className="text-gray-400">Sign in to your account</p></div><div className="rounded-2xl p-8" style={S.card}>{error&&<div className="mb-6 px-4 py-3 rounded-xl flex items-center gap-3" style={{background:'rgba(239,68,68,0.1)',border:'1px solid rgba(239,68,68,0.3)'}}><AlertCircle className="w-5 h-5 text-red-400 shrink-0"/><span className="text-sm text-red-300">{error}</span></div>}<div className="mb-6"><label className="block text-sm font-medium text-gray-300 mb-2">Email</label><input type="email" value={fd.email} onChange={e=>{setFd({...fd,email:e.target.value});setError('')}} onKeyDown={kd} style={S.input} className="w-full px-4 py-3 rounded-xl" placeholder="you@example.com"/></div><div className="mb-6"><label className="block text-sm font-medium text-gray-300 mb-2">Password</label><input type="password" value={fd.password} onChange={e=>{setFd({...fd,password:e.target.value});setError('')}} onKeyDown={kd} style={S.input} className="w-full px-4 py-3 rounded-xl" placeholder="Your password"/></div><button onClick={go} style={S.btnP} className="w-full py-4 rounded-xl font-semibold flex items-center justify-center gap-2 text-white">Sign In<ArrowRight className="w-5 h-5"/></button><div className="mt-6 pt-6 border-t border-white/10 text-center"><p className="text-gray-400 text-sm">Don't have an account? <button onClick={()=>onNavigate('signup')} className="text-purple-400 hover:text-purple-300" style={{cursor:'pointer'}}>Sign up free</button></p></div></div></div></div>)}

function Dashboard({user,setUser,groups,billing,onNavigate,onRefresh,onLogout}){const[showLink,setShowLink]=useState(false);const[linkCode,setLinkCode]=useState('');const[linkError,setLinkError]=useState('');const[linkLoading,setLinkLoading]=useState(false);const[linkSuccess,setLinkSuccess]=useState('');const[copied,setCopied]=useState(false);
if(!user&&!localStorage.getItem('linguaToken')){onNavigate('login');return null}
if(!user){return(<div className="min-h-screen flex items-center justify-center"><div className="text-gray-400">Loading dashboard...</div></div>)}
const copyRef=()=>{navigator.clipboard?.writeText?.(`https://linguabot.ai/ref/${user.referralCode}`);setCopied(true);setTimeout(()=>setCopied(false),2000)};
const toggleGroup=async(chatId,active)=>{try{await api('/api/groups/toggle',{method:'POST',body:JSON.stringify({chatId,active})});await onRefresh()}catch(e){console.error(e)}};
const linkGroup=async()=>{if(!linkCode.trim())return;setLinkLoading(true);setLinkError('');setLinkSuccess('');try{const data=await api('/api/groups/link',{method:'POST',body:JSON.stringify({code:linkCode.trim().toUpperCase()})});setLinkSuccess(`Linked "${data.group.name}" successfully!`);setLinkCode('');await onRefresh();setTimeout(()=>{setShowLink(false);setLinkSuccess('')},2000)}catch(e){setLinkError(e.message)}finally{setLinkLoading(false)}};
const activeGroups=groups?.filter(g=>g.active)||[];const totalMsgs=billing?.totalMessages||0;
return(<div className="min-h-screen pt-24 pb-12 px-6 relative"><div className="absolute top-0 left-1/4 w-[600px] h-[400px] bg-purple-500/5 rounded-full blur-[150px]"/><div className="max-w-6xl mx-auto relative z-10"><div className="flex flex-col md:flex-row items-start md:items-center justify-between gap-4 mb-8"><div><h1 className="text-3xl font-bold mb-2">Welcome back, {user.name}</h1><p className="text-gray-400">Manage your translator bot and billing</p></div><div className="flex items-center gap-4"><button onClick={()=>onNavigate('billing')} style={S.btnS} className="px-4 py-2.5 rounded-xl font-medium flex items-center gap-2 text-sm text-white"><CreditCard className="w-4 h-4"/>Add Hours</button><button onClick={()=>setShowLink(true)} style={S.btnP} className="px-4 py-2.5 rounded-xl font-medium flex items-center gap-2 text-sm text-white"><MessageCircle className="w-4 h-4"/>Link Group</button></div></div>
{user.balance<=0&&<div className="mb-6 px-5 py-4 rounded-xl flex items-center gap-3" style={{background:'rgba(239,68,68,0.1)',border:'1px solid rgba(239,68,68,0.3)'}}><AlertCircle className="w-5 h-5 text-red-400 shrink-0"/><div><span className="text-sm text-red-300 font-medium">Balance depleted â€” bot paused.</span><span className="text-sm text-red-300/70 ml-1">Add hours to resume translation.</span></div></div>}
<div className="grid md:grid-cols-4 gap-4 mb-8"><div style={S.card} className="rounded-2xl p-6"><div className="text-sm text-gray-400 mb-2">Balance</div><div className={`text-3xl font-bold ${user.balance<=1?'text-red-400':'gradient-text'}`}>{user.balance} hrs</div><div className="text-sm text-gray-500 mt-1">${(user.balance||0).toFixed(2)} value</div></div><div style={S.card} className="rounded-2xl p-6"><div className="text-sm text-gray-400 mb-2">Active Groups</div><div className="flex items-center gap-2"><div className={`w-2.5 h-2.5 rounded-full ${activeGroups.length>0?'bg-green-400 animate-pulse':'bg-gray-500'}`}/><span className="text-xl font-bold">{activeGroups.length}</span></div><div className="text-sm text-gray-500 mt-1">{groups?.length||0} total linked</div></div><div style={S.card} className="rounded-2xl p-6"><div className="text-sm text-gray-400 mb-2">Messages Translated</div><div className="text-3xl font-bold">{totalMsgs}</div><div className="text-sm text-gray-500 mt-1">Across all groups</div></div><div style={S.card} className="rounded-2xl p-6"><div className="text-sm text-gray-400 mb-2">Burn Rate</div><div className="text-3xl font-bold text-orange-400">{billing?.burnRate||0}/hr</div><div className="text-sm text-gray-500 mt-1">{activeGroups.length>0?`${activeGroups.length} group Ã— 6 blocks/hr`:'No active groups'}</div></div></div>
<div className="grid lg:grid-cols-3 gap-6"><div className="lg:col-span-2 space-y-6">
<div style={S.card} className="rounded-2xl p-6"><h2 className="text-xl font-semibold mb-6 flex items-center gap-2"><Globe className="w-5 h-5 text-purple-400"/>Linked Telegram Groups</h2>
{(!groups||groups.length===0)?(<div className="text-center py-12"><div className="w-16 h-16 rounded-full bg-purple-500/10 flex items-center justify-center mx-auto mb-4"><MessageCircle className="w-8 h-8 text-purple-400"/></div><h3 className="font-semibold mb-2">No groups linked yet</h3><p className="text-gray-400 text-sm mb-4">Add @clawdtranslatebot to a Telegram group, send /start, then link it here.</p><button onClick={()=>setShowLink(true)} style={S.btnP} className="px-6 py-2.5 rounded-xl text-white text-sm font-medium">Link a Group</button></div>):(
<div className="space-y-3">{groups.map(g=>(<div key={g.chatId} className="flex items-center justify-between p-4 bg-white/5 rounded-xl border border-white/5"><div className="flex items-center gap-4"><div className={`w-10 h-10 rounded-full flex items-center justify-center ${g.active?'bg-green-500/20':'bg-gray-500/20'}`}><MessageCircle className={`w-5 h-5 ${g.active?'text-green-400':'text-gray-400'}`}/></div><div><div className="font-medium text-sm">{g.name}</div><div className="text-xs text-gray-500">{g.messages} messages Â· {g.blocks} blocks Â· {g.lastActivity?new Date(g.lastActivity).toLocaleString():'No activity'}</div></div></div><div className="flex items-center gap-3"><div className={`px-2.5 py-1 rounded-full text-xs ${g.active?'bg-green-500/20 text-green-400':'bg-gray-500/20 text-gray-400'}`}>{g.active?'Active':'Paused'}</div><button onClick={()=>toggleGroup(g.chatId,!g.active)} className={`p-2 rounded-lg hover:bg-white/10 transition-all`} style={{cursor:'pointer'}}><Power className={`w-4 h-4 ${g.active?'text-red-400':'text-green-400'}`}/></button></div></div>))}</div>
)}</div>
<div style={S.card} className="rounded-2xl p-6"><h2 className="text-xl font-semibold mb-4 flex items-center gap-2"><Clock className="w-5 h-5 text-cyan-400"/>How It Works</h2><div className="space-y-3">{['Add @clawdtranslatebot to any Telegram group','Send /start in the group to get a link code','Click "Link Group" above and enter the code','Bot translates all messages â€” usage tracked here','Each 10-min active block = 1 hour from your balance'].map((t,i)=>(<div key={i} className="flex items-center gap-3 p-3 bg-white/5 rounded-xl"><div className="w-7 h-7 rounded-full bg-purple-500/20 flex items-center justify-center text-xs font-bold text-purple-400 shrink-0">{i+1}</div><span className="text-sm text-gray-300">{t}</span></div>))}</div></div>
</div><div className="space-y-6">
<div style={S.card} className="rounded-2xl p-6"><h2 className="text-xl font-semibold mb-4 flex items-center gap-2"><Gift className="w-5 h-5 text-pink-400"/>Invite Friends</h2><p className="text-gray-400 text-sm mb-4">Share your link and you both get 1 free hour!</p><div className="bg-white/5 rounded-xl p-3 flex items-center justify-between mb-4 border border-white/5"><span className="font-mono text-sm text-gray-300 truncate">linguabot.ai/ref/{user.referralCode}</span><button onClick={copyRef} className="ml-2 p-2 hover:bg-white/10 rounded-lg transition-all" style={{cursor:'pointer'}}><Copy className={`w-4 h-4 ${copied?'text-green-400':'text-gray-400'}`}/></button></div>{copied&&<div className="text-sm text-green-400 text-center mb-4">Link copied!</div>}</div>
<div style={S.card} className="rounded-2xl p-6"><h2 className="text-xl font-semibold mb-4 flex items-center gap-2"><CreditCard className="w-5 h-5 text-cyan-400"/>Balance</h2><div className="text-center py-6 border-b border-white/5 mb-6"><div className="text-sm text-gray-400 mb-1">Available Balance</div><div className={`text-4xl font-bold ${user.balance<=1?'text-red-400':'gradient-text'}`}>{user.balance} hrs</div><div className="text-gray-500 text-sm">${(user.balance||0).toFixed(2)}</div></div><div className="text-sm text-gray-500 mb-4 space-y-1"><div className="flex justify-between"><span>Hours used</span><span className="text-white">{user.hoursUsed||0}</span></div><div className="flex justify-between"><span>Active groups</span><span className="text-white">{activeGroups.length}</span></div><div className="flex justify-between"><span>Burn rate</span><span className="text-orange-400">{billing?.burnRate||0} hrs/hr</span></div></div><button onClick={()=>onNavigate('billing')} style={S.btnP} className="text-white w-full py-3 rounded-xl font-medium flex items-center justify-center gap-2 text-sm"><Plus className="w-4 h-4"/>Add Hours</button></div>
</div></div></div>
{showLink&&<div className="fixed inset-0 z-50 flex items-center justify-center p-4" style={{background:'rgba(0,0,0,0.85)',backdropFilter:'blur(12px)'}}><div className="rounded-2xl w-full max-w-md overflow-hidden" style={S.modal}><div className="px-6 pt-6 pb-2 flex items-center justify-between"><h2 className="text-xl font-bold">Link Telegram Group</h2><button onClick={()=>{setShowLink(false);setLinkError('');setLinkSuccess('');setLinkCode('')}} className="text-gray-500 hover:text-white" style={{cursor:'pointer'}}><X className="w-5 h-5"/></button></div><div className="px-6 pb-6"><p className="text-gray-400 text-sm mb-4">Send <span className="text-white font-mono">/start</span> in your Telegram group, then enter the 6-character code below.</p>{linkError&&<div className="mb-4 px-4 py-3 rounded-xl flex items-center gap-3" style={{background:'rgba(239,68,68,0.1)',border:'1px solid rgba(239,68,68,0.3)'}}><AlertCircle className="w-4 h-4 text-red-400 shrink-0"/><span className="text-sm text-red-300">{linkError}</span></div>}{linkSuccess&&<div className="mb-4 px-4 py-3 rounded-xl flex items-center gap-3" style={{background:'rgba(34,197,94,0.1)',border:'1px solid rgba(34,197,94,0.3)'}}><Check className="w-4 h-4 text-green-400 shrink-0"/><span className="text-sm text-green-300">{linkSuccess}</span></div>}<input type="text" value={linkCode} onChange={e=>setLinkCode(e.target.value.toUpperCase().slice(0,6))} onKeyDown={e=>{if(e.key==='Enter')linkGroup()}} style={S.input} className="w-full px-4 py-4 rounded-xl text-center text-2xl font-mono tracking-[0.5em] mb-4" placeholder="ABC123" maxLength={6}/><button onClick={linkGroup} disabled={linkLoading||linkCode.length<6} style={linkCode.length>=6?S.btnP:{...S.btnS,opacity:.5,cursor:'not-allowed'}} className="w-full py-3 rounded-xl font-semibold text-white flex items-center justify-center gap-2">{linkLoading?<><RefreshCw className="w-4 h-4 animate-spin"/>Linking...</>:<>Link Group<ArrowRight className="w-4 h-4"/></>}</button></div></div></div>}
</div>)}

function SetupBotModal({onClose,onBotAssigned}){const[step,setStep]=useState(1);const[sL,setSL]=useState([]);const[model,setModel]=useState('standard');const[botNum,setBotNum]=useState('');const[botId,setBotId]=useState('');const[prov,setProv]=useState(false);const[copied,setCopied]=useState(false);
const langs=[{code:'vi',name:'Vietnamese',flag:'ðŸ‡»ðŸ‡³'},{code:'zh',name:'Chinese',flag:'ðŸ‡¨ðŸ‡³'},{code:'es',name:'Spanish',flag:'ðŸ‡ªðŸ‡¸'},{code:'fr',name:'French',flag:'ðŸ‡«ðŸ‡·'},{code:'ja',name:'Japanese',flag:'ðŸ‡¯ðŸ‡µ'},{code:'ko',name:'Korean',flag:'ðŸ‡°ðŸ‡·'},{code:'de',name:'German',flag:'ðŸ‡©ðŸ‡ª'},{code:'pt',name:'Portuguese',flag:'ðŸ‡§ðŸ‡·'},{code:'ar',name:'Arabic',flag:'ðŸ‡¸ðŸ‡¦'},{code:'hi',name:'Hindi',flag:'ðŸ‡®ðŸ‡³'},{code:'th',name:'Thai',flag:'ðŸ‡¹ðŸ‡­'},{code:'ru',name:'Russian',flag:'ðŸ‡·ðŸ‡º'}];
const models=[{id:'fast',name:'Fast',desc:'Quick casual translation',speed:'~1s',cost:'$0.50/hr',icon:'âš¡'},{id:'standard',name:'Standard',desc:'Balanced speed & accuracy',speed:'~2s',cost:'$1.00/hr',icon:'âš–ï¸'},{id:'premium',name:'Premium',desc:'Best quality, context-aware',speed:'~3s',cost:'$2.00/hr',icon:'âœ¨'}];
const togL=(c)=>setSL(p=>p.includes(c)?p.filter(x=>x!==c):[...p,c]);
const BOT_POOL=[{id:'bot-01',num:'+1 (415) 555-0141',instance:'i-0a1b2c3d4e5f6a01',region:'us-west-2'},{id:'bot-02',num:'+1 (415) 555-0142',instance:'i-0a1b2c3d4e5f6a02',region:'us-west-2'},{id:'bot-03',num:'+1 (415) 555-0143',instance:'i-0a1b2c3d4e5f6a03',region:'us-east-1'},{id:'bot-04',num:'+1 (415) 555-0144',instance:'i-0a1b2c3d4e5f6a04',region:'us-east-1'},{id:'bot-05',num:'+1 (415) 555-0145',instance:'i-0a1b2c3d4e5f6a05',region:'eu-west-1'}];
const provision=()=>{setProv(true);setTimeout(()=>{const available=BOT_POOL[Math.floor(Math.random()*BOT_POOL.length)];setBotNum(available.num);setBotId(available.id);setProv(false);setStep(3);if(onBotAssigned)onBotAssigned({botId:available.id,botNum:available.num,instance:available.instance,region:available.region,languages:sL,model})},3000)};
const copyNum=()=>{navigator.clipboard?.writeText?.(botNum.replace(/[^+\d]/g,''));setCopied(true);setTimeout(()=>setCopied(false),2000)};

return(<div className="fixed inset-0 z-50 flex items-center justify-center p-4" style={{background:'rgba(0,0,0,0.85)',backdropFilter:'blur(12px)'}}><div className="rounded-2xl w-full max-w-lg overflow-hidden max-h-[90vh] overflow-y-auto" style={{...S.modal,scrollbarWidth:'none'}}><div className="px-6 pt-6 pb-4"><div className="flex items-center justify-between mb-1"><h2 className="text-xl font-bold">Set Up Translator Bot</h2><button onClick={onClose} className="text-gray-500 hover:text-white" style={{cursor:'pointer'}}><X className="w-5 h-5"/></button></div><div className="flex items-center gap-2 mt-4">{[1,2,3].map(s=>(<div key={s} className="flex items-center gap-2 flex-1"><div className={`w-7 h-7 rounded-full flex items-center justify-center text-xs font-bold ${step>=s?'bg-purple-500 text-white':'text-gray-500'}`} style={step>=s?{}:S.input}>{step>s?<Check className="w-3.5 h-3.5"/>:s}</div><span className={`text-xs hidden sm:block ${step>=s?'text-white':'text-gray-500'}`}>{s===1?'Languages':s===2?'Model':'Connect'}</span>{s<3&&<div className={`flex-1 h-px ${step>s?'bg-purple-500':'bg-white/10'}`}/>}</div>))}</div></div>
<div className="px-6 pb-6">
{step===1&&<div><p className="text-gray-400 text-sm mb-4">Which languages are in your group chat?</p><div className="grid grid-cols-3 gap-2 mb-2 max-h-[280px] overflow-y-auto" style={{scrollbarWidth:'none'}}>{langs.map(l=>(<button key={l.code} onClick={()=>togL(l.code)} className={`flex items-center gap-2 px-3 py-2.5 rounded-xl text-sm transition-all text-left ${sL.includes(l.code)?'ring-2 ring-purple-500 text-white':'text-gray-300 hover:text-white'}`} style={sL.includes(l.code)?{background:'rgba(168,85,247,0.15)',cursor:'pointer'}:{...S.input,cursor:'pointer'}}><span className="text-lg">{l.flag}</span><span className="truncate">{l.name}</span></button>))}</div>{sL.length>0&&<p className="text-xs text-purple-400 mb-4 mt-3">{sL.length} selected â€” English always included</p>}<button onClick={()=>sL.length>0&&setStep(2)} style={sL.length>0?S.btnP:{...S.btnS,opacity:.5,cursor:'not-allowed'}} className="w-full py-3 rounded-xl font-semibold text-white flex items-center justify-center gap-2 mt-2">Continue<ArrowRight className="w-4 h-4"/></button></div>}
{step===2&&<div><p className="text-gray-400 text-sm mb-4">Choose a translation model. You can switch anytime.</p><div className="space-y-3 mb-6">{models.map(m=>(<button key={m.id} onClick={()=>setModel(m.id)} className={`w-full text-left px-4 py-4 rounded-xl transition-all ${model===m.id?'ring-2 ring-purple-500':''}`} style={model===m.id?{background:'rgba(168,85,247,0.12)',cursor:'pointer'}:{...S.input,cursor:'pointer'}}><div className="flex items-center justify-between"><div className="flex items-center gap-3"><span className="text-2xl">{m.icon}</span><div><div className="font-semibold text-white">{m.name}</div><div className="text-xs text-gray-400">{m.desc}</div></div></div><div className="text-right"><div className="text-sm font-mono text-white">{m.cost}</div><div className="text-xs text-gray-500">{m.speed}</div></div></div></button>))}</div><div className="flex gap-3"><button onClick={()=>setStep(1)} style={S.btnS} className="px-6 py-3 rounded-xl text-white font-medium">Back</button><button onClick={provision} style={S.btnP} className="flex-1 py-3 rounded-xl font-semibold text-white flex items-center justify-center gap-2">{prov?<><RefreshCw className="w-4 h-4 animate-spin"/>Assigning bot...</>:<>Create Bot<Zap className="w-4 h-4"/></>}</button></div></div>}
{step===3&&<div><div className="flex items-center justify-center mb-4"><div className="w-16 h-16 rounded-full bg-green-500/20 flex items-center justify-center"><Check className="w-8 h-8 text-green-400"/></div></div><h3 className="text-xl font-bold text-center mb-1">Your bot is ready!</h3><p className="text-gray-400 text-sm text-center mb-5">Add this number to your Telegram group to start translating.</p><div className="rounded-xl p-4 mb-4" style={{background:'rgba(168,85,247,0.1)',border:'1px solid rgba(168,85,247,0.25)'}}><div className="text-xs text-purple-400 mb-1 font-medium">Bot's Telegram username</div><div className="flex items-center justify-between"><span className="text-2xl font-mono font-bold text-white">{botNum}</span><button onClick={copyNum} style={{cursor:'pointer'}} className="text-purple-400 hover:text-purple-300 p-2">{copied?<Check className="w-5 h-5 text-green-400"/>:<Copy className="w-5 h-5"/>}</button></div></div><div className="rounded-xl p-4 mb-4 space-y-3" style={S.input}>{['Copy the bot username above','Open your Telegram group','Tap group name â†’ Add Members','Search for the bot and add it','Send any message â€” translations start automatically'].map((t,i)=>(<div key={i} className="flex items-center gap-3"><div className="w-6 h-6 rounded-full bg-purple-500/20 flex items-center justify-center text-xs font-bold text-purple-400 shrink-0">{i+1}</div><span className="text-sm text-gray-300">{t}</span></div>))}</div>
<div className="rounded-xl p-4 mb-4" style={{background:'rgba(249,115,22,0.08)',border:'1px solid rgba(249,115,22,0.2)'}}><div className="text-xs text-orange-400 font-medium mb-2 flex items-center gap-1.5"><Clock className="w-3 h-3"/>Billing</div><div className="text-sm text-gray-300">Your bot is <strong className="text-white">always on</strong>. Usage is billed in 10-minute blocks â€” each block costs 1 hour from your balance. The bot will pause when your balance hits zero.</div></div>
<div className="flex items-center justify-between text-xs text-gray-500 mb-4 px-1"><span>English + {sL.map(c=>langs.find(l=>l.code===c)?.flag).join(' ')}</span><span>Model: {models.find(m=>m.id===model)?.name}</span></div><button onClick={onClose} style={S.btnP} className="w-full py-3 rounded-xl font-semibold text-white">Done</button></div>}
</div></div></div>)}

function BillingPage({user,setUser,onNavigate,onRefresh}){const[hours,setHours]=useState(5);const[cn,setCn]=useState('');const[exp,setExp]=useState('');const[cvc,setCvc]=useState('');const[at,setAt]=useState(user?.autoTopUp||false);const[mb,setMb]=useState(user?.minimumBalance||0.5);const[ta,setTa]=useState(user?.topUpAmount||5);const[ok,setOk]=useState(false);const[loading,setLoading]=useState(false);if(!user){onNavigate('login');return null}const buy=async()=>{setLoading(true);try{const data=await api('/api/billing/add-hours',{method:'POST',body:JSON.stringify({hours})});setUser(prev=>({...prev,balance:data.balance}));setOk(true);if(onRefresh)await onRefresh();setTimeout(()=>{setOk(false);onNavigate('dashboard')},2000)}catch(e){console.error(e)}finally{setLoading(false)}};const fCn=v=>{const d=v.replace(/\D/g,'').slice(0,16);return d.replace(/(.{4})/g,'$1 ').trim()};const fEx=v=>{const d=v.replace(/\D/g,'').slice(0,4);return d.length>=2?d.slice(0,2)+'/'+d.slice(2):d};
return(<div className="min-h-screen pt-24 pb-12 px-6 relative"><div className="absolute top-0 right-1/4 w-[500px] h-[400px] bg-cyan-500/5 rounded-full blur-[150px]"/><div className="max-w-4xl mx-auto relative z-10"><button onClick={()=>onNavigate('dashboard')} className="flex items-center gap-2 text-gray-400 hover:text-white transition-colors mb-8 group" style={{cursor:'pointer'}}><ChevronRight className="w-4 h-4 rotate-180 group-hover:-translate-x-1 transition-transform"/>Back to Dashboard</button><h1 className="text-3xl font-bold mb-2">Add Translation Hours</h1><p className="text-gray-400 mb-8">$1/hour. Balance never expires.</p><div className="grid lg:grid-cols-2 gap-6"><div className="space-y-6"><div style={S.card} className="rounded-2xl p-6"><h2 className="text-xl font-semibold mb-6">Select Hours</h2><div className="flex items-center justify-center gap-6 mb-6"><button onClick={()=>setHours(Math.max(1,hours-1))} className="w-12 h-12 rounded-full border border-white/20 hover:bg-white/10 flex items-center justify-center" style={{cursor:'pointer'}}><Minus className="w-5 h-5"/></button><div className="text-center"><div className="text-5xl font-bold gradient-text">{hours}</div><div className="text-gray-400 text-sm">hours</div></div><button onClick={()=>setHours(hours+1)} className="w-12 h-12 rounded-full border border-white/20 hover:bg-white/10 flex items-center justify-center" style={{cursor:'pointer'}}><Plus className="w-5 h-5"/></button></div><div className="grid grid-cols-4 gap-2">{[5,10,20,50].map(h=>(<button key={h} onClick={()=>setHours(h)} className={`py-2.5 rounded-xl border text-sm font-medium transition-all ${hours===h?'border-purple-500 bg-purple-500/20 text-purple-300':'border-white/10 hover:bg-white/5'}`} style={{cursor:'pointer'}}>{h} hrs</button>))}</div></div><div style={S.card} className="rounded-2xl p-6"><h2 className="text-xl font-semibold mb-6">Payment Details</h2><div className="mb-4"><label className="block text-sm font-medium text-gray-300 mb-2">Card Number</label><input type="text" value={cn} onChange={e=>setCn(fCn(e.target.value))} maxLength={19} style={S.input} className="w-full px-4 py-3 rounded-xl font-mono" placeholder="1234 5678 9012 3456"/></div><div className="grid grid-cols-2 gap-4 mb-6"><div><label className="block text-sm font-medium text-gray-300 mb-2">Expiry</label><input type="text" value={exp} onChange={e=>setExp(fEx(e.target.value))} maxLength={5} style={S.input} className="w-full px-4 py-3 rounded-xl font-mono" placeholder="MM/YY"/></div><div><label className="block text-sm font-medium text-gray-300 mb-2">CVC</label><input type="text" value={cvc} onChange={e=>setCvc(e.target.value.replace(/\D/g,'').slice(0,4))} maxLength={4} style={S.input} className="w-full px-4 py-3 rounded-xl font-mono" placeholder="123"/></div></div><button onClick={buy} disabled={loading} style={S.btnP} className="w-full py-4 rounded-xl font-semibold text-lg text-white">{loading?'Processing...':'Pay $'+hours+'.00'}</button></div></div><div className="space-y-6"><div style={S.card} className="rounded-2xl p-6"><h2 className="text-xl font-semibold mb-6">Order Summary</h2><div className="space-y-4 mb-6"><div className="flex items-center justify-between"><span className="text-gray-400">{hours} translation hours</span><span className="font-mono">${hours}.00</span></div><div className="flex items-center justify-between text-sm"><span className="text-gray-500">Rate</span><span className="text-gray-500">$1.00/hour</span></div></div><div className="border-t border-white/10 pt-4"><div className="flex items-center justify-between text-xl"><span className="font-semibold">Total</span><span className="font-bold gradient-text">${hours}.00</span></div></div><div className="mt-6 p-4 bg-green-500/10 border border-green-500/20 rounded-xl"><div className="flex items-center gap-2 text-green-400"><Check className="w-5 h-5"/><span className="font-medium">New balance</span></div><div className="text-2xl font-bold mt-1">{user.balance+hours} hours</div></div></div><div style={S.card} className="rounded-2xl p-6"><div className="flex items-center justify-between mb-6"><h2 className="text-xl font-semibold">Auto Top-up</h2><button onClick={()=>setAt(!at)} className={`w-12 h-6 rounded-full transition-all ${at?'bg-purple-500':'bg-white/20'} relative`} style={{cursor:'pointer'}}><div className={`absolute top-1 w-4 h-4 rounded-full bg-white transition-all ${at?'left-7':'left-1'}`}/></button></div>{at&&<div className="space-y-4"><div><label className="block text-sm font-medium text-gray-300 mb-2">When below</label><div className="flex items-center gap-2"><input type="number" value={mb} onChange={e=>setMb(parseFloat(e.target.value)||0)} step="0.5" min="0.5" style={S.input} className="flex-1 px-4 py-3 rounded-xl"/><span className="text-gray-400">hrs</span></div></div><div><label className="block text-sm font-medium text-gray-300 mb-2">Top up</label><div className="flex items-center gap-2"><input type="number" value={ta} onChange={e=>setTa(parseInt(e.target.value)||0)} step="1" min="1" style={S.input} className="flex-1 px-4 py-3 rounded-xl"/><span className="text-gray-400">hrs</span></div></div></div>}</div><div className="flex items-start gap-3 text-sm text-gray-500"><Shield className="w-5 h-5 mt-0.5 text-cyan-400"/><p>Payment secured with 256-bit encryption.</p></div></div></div></div>{ok&&<div className="fixed inset-0 z-50 flex items-center justify-center p-6" style={{background:'rgba(0,0,0,0.8)'}}><div className="rounded-2xl p-8 max-w-sm w-full text-center" style={{...S.card,border:'1px solid rgba(255,255,255,0.1)'}}><div className="w-16 h-16 rounded-full bg-green-500/20 flex items-center justify-center mx-auto mb-4"><Check className="w-8 h-8 text-green-400"/></div><h2 className="text-2xl font-bold mb-2">Payment Successful!</h2><p className="text-gray-400">{hours} hours added</p></div></div>}</div>)}

function AboutPage({onNavigate}){return(<div className="min-h-screen"><div className="relative pt-32 pb-20 overflow-hidden"><div className="mesh-gradient"/><div className="relative z-10 max-w-4xl mx-auto px-6"><div className="inline-flex items-center gap-2 px-3 py-1 rounded-full bg-purple-500/10 border border-purple-500/20 text-purple-400 text-sm mb-6"><Globe className="w-4 h-4"/>About Us</div><h1 className="text-4xl md:text-6xl font-bold mb-6">Breaking language barriers,<br/><span className="gradient-text">one message at a time</span></h1><p className="text-lg text-gray-400 max-w-2xl leading-relaxed">LinguaBot was founded in 2024 with a simple mission: make real-time, high-quality translation accessible to everyone through the messaging app they already use â€” Telegram.</p></div></div><div className="max-w-4xl mx-auto px-6 pb-20"><div className="grid md:grid-cols-2 gap-12 mb-20"><div style={S.card} className="rounded-2xl p-8"><h3 className="text-xl font-bold mb-4">Our Mission</h3><p className="text-gray-400 leading-relaxed">We believe language should never be a barrier to connection. Whether you're coordinating with a global team or chatting with friends abroad, communication should be effortless.</p></div><div style={S.card} className="rounded-2xl p-8"><h3 className="text-xl font-bold mb-4">Our Approach</h3><p className="text-gray-400 leading-relaxed">Unlike traditional translation tools, LinguaBot lives inside your conversations. Each user gets a dedicated AI instance with contextual memory that improves over time.</p></div></div><div className="mb-20"><h2 className="text-3xl font-bold mb-8">Our Values</h2><div className="grid sm:grid-cols-3 gap-6">{[{t:'Privacy First',d:'Dedicated instances, end-to-end encryption, and data erasure on demand.',i:<Lock className="w-6 h-6 text-green-400"/>},{t:'Accessible Pricing',d:'Pay-per-use at $1/hour. No subscriptions. First hour always free.',i:<CreditCard className="w-6 h-6 text-cyan-400"/>},{t:'Human-Quality AI',d:'Context-aware translations that understand idioms, tone, and cultural nuances.',i:<Brain className="w-6 h-6 text-purple-400"/>}].map((v,i)=>(<div key={i} style={S.card} className="rounded-2xl p-6"><div className="mb-4">{v.i}</div><h3 className="font-semibold mb-2">{v.t}</h3><p className="text-sm text-gray-400 leading-relaxed">{v.d}</p></div>))}</div></div><div className="mb-20"><h2 className="text-3xl font-bold mb-8">The Team</h2><div className="grid sm:grid-cols-3 gap-6">{[{n:'Sarah Lin',r:'CEO & Co-founder',bg:'from-purple-500 to-indigo-500'},{n:'Marcus Chen',r:'CTO & Co-founder',bg:'from-cyan-500 to-blue-500'},{n:'Elena Rodriguez',r:'Head of AI',bg:'from-orange-500 to-pink-500'}].map((p,i)=>(<div key={i} style={S.card} className="rounded-2xl p-6 text-center"><div className={`w-16 h-16 rounded-full bg-gradient-to-br ${p.bg} flex items-center justify-center mx-auto mb-4 text-2xl font-bold`}>{p.n[0]}</div><div className="font-semibold">{p.n}</div><div className="text-sm text-gray-500">{p.r}</div></div>))}</div></div><div className="text-center py-12 rounded-2xl" style={{background:'linear-gradient(135deg,rgba(168,85,247,0.08) 0%,rgba(6,182,212,0.05) 100%)',border:'1px solid rgba(168,85,247,0.15)'}}><h3 className="text-2xl font-bold mb-3">Want to join us?</h3><p className="text-gray-400 mb-6">We're always looking for talented people passionate about AI and language.</p><button style={S.btnP} className="text-white px-8 py-3 rounded-xl font-medium">View Open Positions</button></div></div><Foot onNavigate={onNavigate}/></div>)}

function BlogPage({onNavigate}){const posts=[{t:'How AI is Revolutionizing Real-Time Translation',e:'Explore how modern language models enable instant, context-aware translations that rival human interpreters.',d:'Jan 28, 2026',tag:'AI & ML',gr:'from-purple-500 to-indigo-500'},{t:'Why Context Matters More Than Vocabulary',e:'Word-for-word translation fails because language is contextual. Learn how LinguaBot uses conversation memory.',d:'Jan 15, 2026',tag:'Product',gr:'from-cyan-500 to-blue-500'},{t:'Building Scalable Telegram Bot Infrastructure',e:'A deep dive into dedicated bot instances with sub-second response times across 100+ languages.',d:'Jan 3, 2026',tag:'Engineering',gr:'from-orange-500 to-red-500'},{t:'5 Tips for Multilingual Telegram Groups',e:'Practical advice for teams communicating across language barriers effectively.',d:'Dec 20, 2025',tag:'Tips',gr:'from-green-500 to-emerald-500'},{t:'Year in Review: 10M Messages Translated',e:'A look back at our first year serving 150K+ users across 80 countries.',d:'Dec 10, 2025',tag:'Company',gr:'from-pink-500 to-rose-500'},{t:'Introducing Model Selection: Fast, Standard, Premium',e:'Three translation tiers for the right balance of speed, cost, and quality.',d:'Nov 28, 2025',tag:'Launch',gr:'from-indigo-500 to-violet-500'}];
return(<div className="min-h-screen"><div className="relative pt-32 pb-16 overflow-hidden"><div className="mesh-gradient"/><div className="relative z-10 max-w-4xl mx-auto px-6"><div className="inline-flex items-center gap-2 px-3 py-1 rounded-full bg-cyan-500/10 border border-cyan-500/20 text-cyan-400 text-sm mb-6"><BookOpen className="w-4 h-4"/>Blog</div><h1 className="text-4xl md:text-6xl font-bold mb-6">Latest from <span className="gradient-text">LinguaBot</span></h1><p className="text-lg text-gray-400">Insights on AI translation, product updates, and tips.</p></div></div><div className="max-w-4xl mx-auto px-6 pb-20"><div className="grid md:grid-cols-2 gap-6">{posts.map((p,i)=>(<div key={i} style={S.card} className="rounded-2xl overflow-hidden hover:scale-[1.01] transition-all duration-300 cursor-pointer"><div className={`h-2 bg-gradient-to-r ${p.gr}`}/><div className="p-6"><div className="flex items-center gap-3 mb-4"><span className="text-xs px-2.5 py-1 rounded-full bg-white/5 text-gray-400">{p.tag}</span><span className="text-xs text-gray-500">{p.d}</span></div><h3 className="text-lg font-semibold mb-3 leading-tight">{p.t}</h3><p className="text-sm text-gray-400 leading-relaxed">{p.e}</p><button className="mt-4 text-sm text-purple-400 hover:text-purple-300 flex items-center gap-1" style={{cursor:'pointer'}}>Read more <ArrowRight className="w-3 h-3"/></button></div></div>))}</div></div><Foot onNavigate={onNavigate}/></div>)}

function TermsPage({onNavigate}){return(<div className="min-h-screen"><div className="relative pt-32 pb-16 overflow-hidden"><div className="mesh-gradient"/><div className="relative z-10 max-w-3xl mx-auto px-6"><div className="inline-flex items-center gap-2 px-3 py-1 rounded-full bg-orange-500/10 border border-orange-500/20 text-orange-400 text-sm mb-6"><FileText className="w-4 h-4"/>Legal</div><h1 className="text-4xl md:text-5xl font-bold mb-4">Terms of Service</h1><p className="text-gray-500 text-sm">Last updated: January 1, 2026</p></div></div><div className="max-w-3xl mx-auto px-6 pb-20 space-y-8">{[{t:'1. Acceptance of Terms',b:'By accessing or using LinguaBot, you agree to these Terms of Service. We may modify these terms at any time with notice via email or in-app notification.'},{t:'2. Service Description',b:'LinguaBot provides AI-powered real-time translation within Telegram group conversations. Each user receives a dedicated translator instance. Quality depends on the selected model tier.'},{t:'3. Account & Registration',b:'You must provide accurate information when creating an account. You are responsible for maintaining the confidentiality of your credentials. Accounts are non-transferable.'},{t:'4. Pricing & Billing',b:'Translation hours are billed at $0.50â€“$2.00/hour depending on model tier. First hour is free. Purchased hours never expire. Refunds available within 14 days for unused hours.'},{t:'5. Acceptable Use',b:'You may not use the Service to transmit illegal content, harass others, circumvent security measures, reverse-engineer the translation engine, or resell access.'},{t:'6. Privacy & Data',b:'Translation data is processed on dedicated instances and is not shared with other users or used for training. You may request full data erasure at any time.'},{t:'7. Limitation of Liability',b:'LinguaBot is provided "as is" without warranty. We are not liable for translation errors or service interruptions. Total liability limited to amount paid in preceding 12 months.'},{t:'8. Contact',b:'Questions? Email legal@linguabot.ai or write to LinguaBot Inc., 548 Market St, San Francisco, CA 94104.'}].map((s,i)=>(<div key={i}><h2 className="text-xl font-semibold mb-3">{s.t}</h2><p className="text-gray-400 leading-relaxed">{s.b}</p></div>))}</div><Foot onNavigate={onNavigate}/></div>)}

function PrivacyPage({onNavigate}){return(<div className="min-h-screen"><div className="relative pt-32 pb-16 overflow-hidden"><div className="mesh-gradient"/><div className="relative z-10 max-w-3xl mx-auto px-6"><div className="inline-flex items-center gap-2 px-3 py-1 rounded-full bg-green-500/10 border border-green-500/20 text-green-400 text-sm mb-6"><Shield className="w-4 h-4"/>Legal</div><h1 className="text-4xl md:text-5xl font-bold mb-4">Privacy Policy</h1><p className="text-gray-500 text-sm">Last updated: January 1, 2026</p></div></div><div className="max-w-3xl mx-auto px-6 pb-20 space-y-8">{[{t:'What We Collect',b:'We collect your email, name, and payment info when you create an account. Message content is processed in real-time on your dedicated instance but not permanently stored unless you enable conversation memory.'},{t:'How We Use Your Data',b:'Data is used solely to provide translation, process payments, and send service communications. We never sell your data or use translation content to train AI models.'},{t:'Dedicated Instances',b:'Each user receives an isolated translator instance. Your translation data is not accessible to other users. Privacy by design.'},{t:'Data Retention',b:'Account data retained while active. Translation memory can be cleared anytime from dashboard. Account deletion erases all data within 30 days.'},{t:'Security',b:'All data encrypted in transit (TLS 1.3) and at rest (AES-256). Payment via Stripe â€” we never store full card numbers. SOC 2 Type II certified infrastructure.'},{t:'Third Parties',b:'We use Stripe for payments and cloud providers for hosting. No data shared with advertisers or data brokers.'},{t:'Your Rights',b:'Request data copy, correct inaccuracies, or delete account anytime. EU residents have GDPR rights. California residents have CCPA rights.'},{t:'Contact',b:'Privacy inquiries: privacy@linguabot.ai or LinguaBot Inc., 548 Market St, San Francisco, CA 94104.'}].map((s,i)=>(<div key={i}><h2 className="text-xl font-semibold mb-3">{s.t}</h2><p className="text-gray-400 leading-relaxed">{s.b}</p></div>))}</div><Foot onNavigate={onNavigate}/></div>)}
